import React from 'react'
import Country from './Country'
import Header from './Header'

const CountriesDisplay = ({countries}) => {
  const tooManyReply = 'Too many matches, specify another filter'
  if(countries === tooManyReply) {
    return (
      <div>
        {tooManyReply}       
      </div>
    )
  }
  else if(countries.length === 1) {
    return (
      <div>
        <Header header={countries[0].name.common}/>
        <p>{countries[0].capital[0]}</p>
      </div>
    )
  }

  let display = countries.map((country) => <Country key={country.name.common} name={country.name.common}/>)
  return (
    <div>
      {display}       
    </div>
  )
}


export default CountriesDisplay
