import { useState, useEffect } from 'react'
import NameFilter from './components/NameFilter'
import axios from 'axios'
import CountriesDisplay from './components/CountriesDisplay'

const App = () => {
  const [countries, setCountries] = useState([])
  const [newFilter, setNewFilter] = useState('')

  const handleFilterChange = (event) => {
    setNewFilter(event.target.value)
  }

  const showCountries = () => {
    return countries.filter(country => country.name.common.toLowerCase().includes(newFilter.toLowerCase()))
  }

  useEffect(() => { //as shown in part 2, effect hooks, it the country data
    axios.get('https://restcountries.com/v3.1/all')
      .then(response => {
        setCountries(response.data)
      })
  }, [])

  return (
    <div>
      <NameFilter inputNameValue={newFilter} inputNameChangeFunc={handleFilterChange} />
      <CountriesDisplay countries={showCountries()} />
    </div>
  )
}

export default App
