import React from 'react'
import { useEffect } from 'react'
import Header from './Header'
import Language from './Language'
import axios from 'axios'

const CountryView = ({ country }) => {
  const apikey = 'ebc28604ccdf0d47576db996624f70c9'
  const apiReq = `api.openweathermap.org/data/2.5/weather?q=${country.capital[0]}&appid=${apikey}`

  useEffect(() => { 
    axios.get(apiReq)
      .then(response => {
        console.log(response.data)
      })
  }, [apiReq])

  return (
    <div>
        <Header header={country.name.common}/>
        <p>capital {country.capital[0]}</p>
        <p>area {country.area}</p>
        <h2>languages</h2>
        {Object.values(country.languages).map((language) => <Language key={language} language={language}/>)} {/*Object.values() returns an array of all the language values*/}
        {country.flag}
        <h2>Weather</h2>
      </div>
  )
}


export default CountryView
