import { useState } from 'react'

const Person = ({ name, number }) => {
  return (
    <div>
      <p>
        {name} {number}
      </p>
    </div>
  )
}

const PhoneForm = ({submitFunc, inputValue, inputChangeFunc, inputDesc, buttonDesc}) => {
  return (
    <form onSubmit={submitFunc}>
      <div>
        {inputDesc}: <input value={inputValue} onChange={inputChangeFunc}/>
      </div>
      <div>
        <button type="submit">{buttonDesc}</button>
      </div>
    </form>
  )
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')

  const addPerson = (event) => {
    event.preventDefault()
    if(persons.some(person => person.name === newName)){
      window.alert(`${newName} is already added to the phonebook`)
      return
    }
    const newPerson = {
      name: newName,
      number: newNumber
    }
    setPersons(persons.concat(newPerson))
    setNewName('')
    setNewNumber('')
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <PhoneForm submitFunc={addPerson} inputValue={newName} inputChangeFunc={handleNameChange} inputDesc='name' buttonDesc='add'/>
      <h2>Numbers</h2>
      {persons.map((person) => <Person key={person.name} name={person.name} number={person.number}/>)}
    </div>
  )
}

export default App
